        private bool ? _isEnabledConsoleTarget ;
        private bool ? _isEnabledEventLogTarget ;
        private bool ? _isEnabledCacheTarget ;
        private LogLevel _minLogLevel ;
        private bool ? _isEnabledDebuggerTarget ;
